sys_script_include.script:
var Recaptchav2 = Class.create();
Recaptchav2.prototype = {

	initialize: function() {
	},

	RecaptchaSecret: gs.getProperty('x_60583_pub_form.google.captcha.secret'),

	verify: function(token) {

		try{
			var r = new sn_ws.RESTMessageV2('Google Recaptcha', 'POST');

			r.setStringParameterNoEscape('secret', this.RecaptchaSecret);
			r.setStringParameterNoEscape('response', token);

			var response = r.execute();
			var responseBody = response.getBody();
			var httpStatus = response.getStatusCode();

			return JSON.parse(responseBody);
		}
		catch (ex) {
			var message = ex.message;
		}

	},

	type: 'Recaptchav2'
};
