sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var messages = [];
    var captcha = new x_60583_pub_form.Recaptchav2().verify(request.body.data.recaptchaToken);

    if (captcha.success === true) {

        // Create one row using the fields object from widget
        var feedback = new GlideRecord('x_60583_pub_form_public_feedback');
        feedback.initialize();

        var fields = request.body.data.fields;
        for (f in fields) {
            messages.push(f + "=" + fields[f].value);

            var columnName = f; // adjust column names for global table: 'u_' + f
            feedback[columnName] = fields[f].value;
        }
        if (messages.length > 0) {
            gs.info("Messages: " + messages.join('\n'));

            var feedbackId = feedback.insert();
            if (feedbackId) {
                feedback = new GlideRecord('x_60583_pub_form_public_feedback');
                feedback.get(feedbackId);

                response.setBody({
                    'number': feedback.u_number
                });
            } else {
				// insert failed
				response.setStatus(500);
			}
        } else {
			// column iterations failed
            response.setStatus(500);
        }

    }
	//captcha failed
	else {
		gs.info("SRAPI submit: Captcha failed.");
        response.setStatus(403);
    }

})(request, response);
